import { Component, Input } from '@angular/core';
import { Actor } from '../Actor/alta/alta.component';

@Component({
  selector: 'app-detalle-pais',
  templateUrl: './detalle-pais.component.html',
  styleUrls: ['./detalle-pais.component.css']
})
export class DetallePaisComponent {
  @Input() mostrar: boolean = false; 
  @Input() actor: Actor = new Actor; 
}
<div *ngIf="mostrar">
    <ul>
        <li>Nombre: {{pais.name.common}}</li>
        <li>Region: {{pais.region}}</li>
        <li> <img src={{pais.flags.png}} class="img-thumbnail" alt="...">bandera</li>
    </ul>
</div>
