import { Component, Output, EventEmitter } from '@angular/core';
import Swal from 'sweetalert2';
import { Pelicula } from 'src/app/app.component';
import { getDownloadURL, listAll, ref, Storage } from '@angular/fire/storage';

@Component({
  selector: 'app-tabla-pelicula',
  templateUrl: './tabla-pelicula.component.html',
  styleUrls: ['./tabla-pelicula.component.css']
})
export class TablaPeliculaComponent {
  arrayPelis : Array<Pelicula> = [];
  @Output() newItemEvent = new EventEmitter<Pelicula>(); 

  constructor(private storage : Storage)
  {
    this.TraerLS();
  }

  TraerLS()
  {
    let resultado : string | null = localStorage.getItem("ListaDePelis");
    if(resultado!=null)
    {
      this.arrayPelis = JSON.parse(resultado);
      this.arrayPelis.forEach(pelicula => 
      {
        const imagReferencia = ref(this.storage, pelicula.fotoPelicula);
        getDownloadURL(imagReferencia)
        .then(elemt=>
        {
          pelicula.fotoPelicula = elemt;
        })

      });
    }
    else
    {
      Swal.fire("ERROR!! No se encontro el localStorage");
    }
  }

  PasarPelicula(pelicula : Pelicula)
  {
    this.newItemEvent.emit(pelicula);  
  }
}

<div>
   <table>
    <tr>
        <th>Id</th>
        <th>Nombre</th>
        <th>Tipo</th>
        <th style="width: 10%;">Cantidad de personas</th>
        <th style="width: 13%;">Fecha de estreno</th>
        <th>Foto</th>
        <th style="width: 11%;">Seleccionar pelicula</th>
    </tr>
    <tr *ngFor="let pelicula of arrayPelis">
        <td>{{pelicula.Id}}</td>
        <td>{{pelicula.nombre}}</td>
        <td>{{pelicula.tipo}}</td>
        <td>{{pelicula.cantidadPersonas}}</td>
        <td>{{pelicula.fechaEstreno}}</td>
        <td><img src={{pelicula.fotoPelicula}} width="50%" height="50%"></td>
        <td><button (click)="PasarPelicula(pelicula)">Seleccionar pelicula</button>
    </tr>
   </table>
</div>
