import { Component } from '@angular/core';
import Swal from 'sweetalert2';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {

  ArrayPelis : Array<Pelicula> = new Array;

  GuardarLS()
  {
    this.CrearArray();
    let array : AppComponent[] = this.TraerLS();
    array[array.length] = this;
    localStorage.setItem("ListaDePelis", JSON.stringify(array))
    Swal.fire("ya has sido registrado");
  }

  TraerLS() : any[]
  {
    let array : any[] = [];
    let resultado : string | null = localStorage.getItem("ListaDePelis");
    if(resultado!=null)
    {
      array = JSON.parse(resultado);
    }
    return array;
  }

  CrearArray()
  {
    this.ArrayPelis.push(new Pelicula(1,"monja",new Date("12/4/2004"),"Terror",null,500));
    this.ArrayPelis.push(new Pelicula(2,"Duero de matar",new Date("10/6/2010"),"Acción",null,300));
    this.ArrayPelis.push(new Pelicula(3,"Avengers",new Date("8/10/2014"),"Aventura",null,500));
  }
}

export class Pelicula {
  Id : number;
  nombre: string;
  fechaEstreno : Date;
  tipo: string;
  fotoPelicula : null | string;
  cantidadPersonas: number;

  constructor(id : number, nombre :string, fechaEstreno : Date, tipo: string, fotoPelicula : string | null, cantidadPersonas: number)
  {
    this.Id =id;
    this.cantidadPersonas = cantidadPersonas;
    this.fechaEstreno = fechaEstreno;
    this.fotoPelicula = fotoPelicula;
    this.nombre = nombre;
    this.tipo = tipo;
  }
}
