import { Component } from '@angular/core';
import { Actor } from '../../Actor/alta/alta.component';
import { AbstractControl, FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Pelicula } from 'src/app/app.component';

@Component({
  selector: 'app-alta',
  templateUrl: './alta.component.html',
  styleUrls: ['./alta.component.css']
})
export class AltaComponent {
  public actor : Actor = new Actor()
  public actorNombre : string = "";
  public pelicula : Pelicula = new Pelicula();
  public grupo : FormGroup;
  constructor(private fb : FormBuilder )
  {
    this.grupo = this.fb.group({
      nombre : ["",[Validators.required]],
      tipo :["",[Validators.required]],
      cantidadPersonas :["",[Validators.required]],
      fecha :["",[Validators.required]],
      foto :["",[Validators.required]],
      zip :["",[Validators.required]],
    });
  };

  addItem(event : any)
  {
    this.actor = event;
  }

  TraerLS() : any
  {
    let arrayPeliculas : any = [];
    let resultado : string | null = localStorage.getItem("ListaDePelis");
    if(resultado!=null)
    {
      arrayPeliculas = JSON.parse(resultado);
    }
    return arrayPeliculas;
  }

  Guardar()
  {
    if(this.actor.pais != null)
    {
      if(this.grupo.status == "VALID")
      {
        let arrayPeliculas : any = this.TraerLS();
        this.pelicula.Id =
        arrayPeliculas.push(this.pelicula);
        localStorage.setItem("ListaDePelis", JSON.stringify(arrayPeliculas));
        alert("se guardo la pelicula");
      }
      else
      {
        alert("Hay algun campo incompleto o con algún error");
      }
    }
    else
    {
      alert("Seleccione un actor");
    }
  }
}
<div class="container">
    <main>
      <div class="row g-5 py-5">
        <div class="col-md-5 col-lg-4 order-md-last">
          <h4 class="d-flex justify-content-between align-items-center mb-3">
            <span class="text-primary">Elija un actor: {{actor.nombre}}</span>
          </h4>
          <app-listado-actor (newItemEvent)="addItem($event)"></app-listado-actor>
        </div>

        <div class="col-md-7 col-lg-8">
          <h4 class="mb-3">Registrar Pelicula</h4>
          <form class="needs-validation" [formGroup]="grupo">
            <div class="row g-3">

              <div class="col-sm-6">
                <label class="form-label">Nombre</label>
                <input type="text" class="form-control" id="nombre" [(ngModel)]="pelicula.nombre" formControlName="nombre" >
                <small 
                *ngIf="
                grupo.controls['nombre'].errors?.['required'] && 
                grupo.controls['nombre'].touched 
                ">
                  ERROR!! falta Nombre
                </small>
              </div>

              <div class="col-sm-6">
                <label class="form-label">Tipo</label>
                <input type="text" class="form-control" id="Tipo" [(ngModel)]="pelicula.tipo" formControlName="tipo">
                <small 
                *ngIf="
                  grupo.controls['tipo'].errors?.['required'] && 
                  grupo.controls['tipo'].touched
                ">
                  ERROR!! falta tipo
                </small>
              </div>

              <div class="col-12">
                <label  class="form-label">fecha de estreno</label>
                  <input type="date" class="form-control" id="fecha de estreno" [(ngModel)]="pelicula.fechaEstreno" formControlName="fecha">
                  <small 
                *ngIf="
                  grupo.controls['fecha'].errors?.['required'] && 
                  grupo.controls['fecha'].touched
                ">
                  ERROR!! Elija una fehca
                </small>
              </div>

              <div class="col-12">
                <label class="form-label">foto</label>
                <div>
                  <input type="file" [(ngModel)]="pelicula.fotoPelicula" formControlName="foto">
                  <small 
                  *ngIf="
                    grupo.controls['foto'].errors?.['required'] && 
                    grupo.controls['foto'].touched
                  ">
                    ERROR!! elija una foto
                  </small>
                </div>

              </div>

              <div class="col-12">
                <label class="form-label"> Cantidad de personas</label>
                <input type="number" class="form-control" id="cantidadPersonas" [(ngModel)]="pelicula.cantidadPersonas" formControlName="cantidadPersonas">
                <small 
                *ngIf="
                  grupo.controls['cantidadPersonas'].errors?.['required'] && 
                  grupo.controls['cantidadPersonas'].touched
                ">
                  ERROR!! Elija la cantidad de personas que hay para la pelicula
                </small>
              </div>

            </div>
            <button class="w-100 btn btn-primary btn-lg" (click)="Guardar()">Continuar para registrar</button>
          </form>
        </div>
      </div>
    </main>
  </div>
