import { Component } from '@angular/core';
import { Actor } from '../Actor/alta/alta.component';

@Component({
  selector: 'app-actor-pelicula',
  templateUrl: './actor-pelicula.component.html',
  styleUrls: ['./actor-pelicula.component.css']
})
export class ActorPeliculaComponent {
  public mostrar : boolean = false;
  public actorSelec : Actor = new Actor;
  public componente : 
  ActorSeleccionado(evento : any)
  {
    switch (evento.tipo)
    {
      case "actor":
        console.log(evento.actor);
      break;

      case "peliculas":
        this.mostrar = true;
        this.actorSelec = evento.actor;
      break;

      case "pais":
        console.log(evento.pais);
      break;
    }

  }
}
<div class="mitad">
    <div class="tabla">
        <app-tabla-actores (newItemEvent)="ActorSeleccionado($event)"></app-tabla-actores>
    </div>
</div>
<div class="pelicula" >
   <app-peliculas-actuadas [mostrar]="mostrar" [actor]="actorSelec" ></app-peliculas-actuadas>
</div>
