import { Component } from '@angular/core';
import { TablaPaisesComponent} from 'src/app/components/tabla-paises/tabla-paises.component';
import { AbstractControl, FormBuilder, FormControl, FormGroup, FormsModule, Validators } from '@angular/forms';

@Component({
  selector: 'app-alta-actor',
  templateUrl: './altaActor.component.html',
  styleUrls: ['./altaActor.component.css']
})
export class AltaActorComponent 
{
  public actor : Actor = new Actor()
  public nombre : string = "";
  public apellido : string= "";
  public direccion : string= "";
  public usuario : string= "";
  public pais : any = null; 
  public paisNombre : string = "";
  public email : string= "";
  public estado : string= "";
  public verificaEmail : boolean= false;
  public zip : number= 0;

  public grupo : FormGroup;
  constructor(private fb : FormBuilder )
  {
    this.grupo = this.fb.group({
      nombre : ["",[Validators.required]],
      apellido :["",[Validators.required]],
      usuario :["",[Validators.required]],
      email :["",[Validators.required, Validators.email]],
      zip :["",[Validators.required]],
      direccion :["",[Validators.required]],
    });
  };

  addItem(event : any)
  {
    this.pais = event;
    this.paisNombre = event.name.common;
  }

  TraerLS(arrayActores : any)
  {
    let resultado : string | null = localStorage.getItem("Actor");
    if(resultado!=null)
    {
      arrayActores = JSON.parse(resultado);
    }
  }

  Guardar()
  {
    if(this.pais != null)
    {
      if(this.grupo.status == "VALID")
      {
        let actor = new Actor(this.nombre, this.apellido, this.direccion, this.usuario, this.pais, this.email,this.zip);
        let arrayActores : any = [];
        this.TraerLS(arrayActores);
        arrayActores.push(actor);
        localStorage.setItem("Actor", JSON.stringify(arrayActores));
        alert("se guardo el actor");
      }
      else
      {
        alert("Hay algun campo incompleto o con algún error");
      }
    }
    else
    {
      alert("Seleccione un pais");
    }
  }
}


export class Actor 
{
  nombre : string;
  apellido : string;
  direccion : string;
  usuario : string;
  pais : object;
  email : string;
  zip : number;

  constructor(nombre : string = "", apellido : string  = "", dirección : string  = "", usuario : string  = "", pais : any  = [], 
  email : string  = "", zip : number  = 0)
  {
    this.nombre = nombre;
    this.apellido = apellido;
    this.direccion = dirección;
    this.usuario = usuario;
    this.pais = pais;
    this.email = email;
    this.zip = zip;
  }

}
