import { Component, Output, EventEmitter } from '@angular/core';
import Swal from 'sweetalert2';

@Component({
  selector: 'app-tabla-pelicula',
  templateUrl: './tabla-pelicula.component.html',
  styleUrls: ['./tabla-pelicula.component.css']
})
export class TablaPeliculaComponent {
  arrayPelis : Array<Pelicula> = [];

  TraerLS()
  {
    let resultado : string | null = localStorage.getItem("ListaDePelis");
    if(resultado!=null)
    {
      this.arrayPelis = JSON.parse(resultado);
      console.log(this.arrayPelis);
    }
    else
    {
      Swal.fire("ERROR!! No se encontro el localStorage");
    }
  }


}

export class Pelicula {
  Id : number;
  nombre: string;
  fechaEstreno : Date;
  tipo: string;
  fotoPelicula : null | string;
  cantidadPersonas: number;

  constructor(id : number, nombre :string, fechaEstreno : Date, tipo: string, fotoPelicula : string | null, cantidadPersonas: number)
  {
    this.Id =id;
    this.cantidadPersonas = cantidadPersonas;
    this.fechaEstreno = fechaEstreno;
    this.fotoPelicula = fotoPelicula;
    this.nombre = nombre;
    this.tipo = tipo;
  }
}
