import { Component } from '@angular/core';

@Component({
  selector: 'app-alta',
  templateUrl: './alta.component.html',
  styleUrls: ['./alta.component.css']
})
export class AltaComponent {
  public actor : Actor = new Actor()
  public paisNombre : string = "";

  public grupo : FormGroup;
  constructor(private fb : FormBuilder )
  {
    this.grupo = this.fb.group({
      nombre : ["",[Validators.required]],
      apellido :["",[Validators.required]],
      usuario :["",[Validators.required]],
      email :["",[Validators.required, Validators.email]],
      zip :["",[Validators.required]],
      direccion :["",[Validators.required]],
    });
  };

  addItem(event : any)
  {
    this.actor.pais = event;
    this.paisNombre = event.name.common;
  }

  TraerLS() : any
  {
    let arrayActores : any = [];
    let resultado : string | null = localStorage.getItem("Actor");
    if(resultado!=null)
    {
      arrayActores = JSON.parse(resultado);
    }
    return arrayActores;
  }

  Guardar()
  {
    if(this.actor.pais != null)
    {
      if(this.grupo.status == "VALID")
      {
        let arrayActores : any = this.TraerLS();
        arrayActores.push(this.actor);
        localStorage.setItem("Actor", JSON.stringify(arrayActores));
        alert("se guardo el actor");
      }
      else
      {
        alert("Hay algun campo incompleto o con algún error");
      }
    }
    else
    {
      alert("Seleccione un pais");
    }
  }
}
