import { Component } from '@angular/core';
import { TablaPaisesComponent} from 'src/app/components/tabla-paises/tabla-paises.component';
import { AbstractControl, FormBuilder, FormControl, FormGroup, FormsModule, Validators } from '@angular/forms';

@Component({
  selector: 'app-alta-actor',
  templateUrl: './altaActor.component.html',
  styleUrls: ['./altaActor.component.css']
})
export class AltaActorComponent 
{
  public grupo : FormGroup;
  constructor(private fb : FormBuilder ){
    this.grupo = this.fb.group({
      nombre : ["",[Validators.required, Validators.minLength(3)]],
      apellido :["",[Validators.required, Validators.minLength(3)]],
      usuario :["",[Validators.required, Validators.minLength(3)]],
      email :["",[Validators.required, Validators.minLength(3)]],
      zip :["",[Validators.required, Validators.minLength(3)]],
      direccion :["",[Validators.required, Validators.minLength(3)]]
    });
  };
}


export class Actor 
{
  nombre : string;
  apellido : string;
  dirección : string;
  usuario : string;
  pais : object;
  email : string;
  estado : string;
  zip : number;

  constructor(nombre : string, apellido : string, dirección : string, usuario : string, pais : object, email : string, estado : string, 
    zip : number)
  {
    this.nombre = nombre;
    this.apellido = apellido;
    this.dirección = dirección;
    this.usuario = usuario;
    this.pais = pais;
    this.email = email;
    this.estado = estado;
    this.zip = zip;
  }

}
