import { Component, ViewEncapsulation } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {

  ArrayPelis : Array<Pelicula> = new Array;

  GuardarLS()
  {
    this.CrearArray();
    localStorage.setItem("ListaDePelis", JSON.stringify(this.ArrayPelis))
  }

  CrearArray()
  {
    this.ArrayPelis.push(new Pelicula(1,"monja",new Date(2004,4,12),"Terror",null,500));
    this.ArrayPelis.push(new Pelicula(2,"Duero de matar",new Date(2010,6,10),"Acción",null,300));
    this.ArrayPelis.push(new Pelicula(3,"Avengers",new Date(2014,10,8),"Aventura",null,500));
  }
}

export class Pelicula {
  Id : number;
  nombre: string;
  fechaEstreno : Date;
  tipo: string;
  fotoPelicula : null | string;
  cantidadPersonas: number;

  constructor(id : number =0 , nombre :string="", fechaEstreno : Date=new Date(), tipo: string= "", fotoPelicula : string | null = null, cantidadPersonas: number =0)
  {
    this.Id =id;
    this.cantidadPersonas = cantidadPersonas;
    this.fechaEstreno = fechaEstreno;
    this.fotoPelicula = fotoPelicula;
    this.nombre = nombre;
    this.tipo = tipo;
  }
}
