import { Component, Input } from '@angular/core';
import { Pelicula } from 'src/app/app.component';

@Component({
  selector: 'app-detalle-pelicula',
  templateUrl: './detalle-pelicula.component.html',
  styleUrls: ['./detalle-pelicula.component.css']
})
export class DetallePeliculaComponent 
{
  @Input() mostrar: boolean = false; 
  @Input() pelicula: Pelicula = new Pelicula; 
}
<div *ngIf="mostrar">
    <ul>
        <li>Nombre: {{pelicula.nombre}}</li>
        <li>Tipo: {{pelicula.tipo}}</li>
        <li>Cantidad de personas: {{pelicula.cantidadPersonas}}</li>
        <li>Estreno: {{(pelicula.fechaEstreno).getDay}}</li>
    </ul>
</div>
