import { Component } from '@angular/core';
import { Actor } from '../Actor/alta/alta.component';
import { getDownloadURL, ref } from '@angular/fire/storage';

@Component({
  selector: 'app-tabla-actores',
  templateUrl: './tabla-actores.component.html',
  styleUrls: ['./tabla-actores.component.css']
})
export class TablaActoresComponent {

  public arrayActores : Array<any>;
  constructor(private storage : Storage)
  {
    this.arrayActores = [];
  }

  VerPeliculas(actor : Actor)
  {

  }

  DetallePais(pais : any)
  {

  }

  DetalleActor(actor : Actor)
  {

  }

  TraerLS()
  {
    let resultado : string | null = localStorage.getItem("ListaDePelis");
    if(resultado!=null)
    {
      this.arrayActores = JSON.parse(resultado);
      this.arrayActores.forEach(actor => 
      {
        const imagReferencia = ref(this.storage, actor.fotoPelicula);
        getDownloadURL(imagReferencia)
        .then(elemt=>
        {
          actor.fotoPelicula = elemt;
        })
      });
    }
    else
    {
      Swal.fire("ERROR!! No se encontro el localStorage");
    }
  }
}

<div>
    <table>
     <tr>
         <th>Nombre</th>
         <th>Pais</th>
         <th>Ver peliculas hechas</th>
         <th>Detalle del pais</th>
         <th>Detalle del actor</th>
     </tr>
     <tr *ngFor="let actor of arrayActores">
         <td>{{actor.nombre}} {{actor.apellido}}</td>
         <td>{{actor.pais.name.common}}</td>
         <td><button (click)="VerPeliculas(actor)">Seleccionar actor</button>
         <td><button (click)="DetallePais(actor.pais)">Seleccionar actor</button>
         <td><button (click)="DetalleActor(actor)">Seleccionar actor</button>
     </tr>
    </table>
 </div>
